import { useContext, useEffect, useReducer, useRef } from 'react';

import {
  CalendarEvent,
  Config,
  EventLayout,
  EventStyle,
} from '../../common/interface';
import { Context, Store } from '../../context/store';
import { DateTime } from 'luxon';
import {
  EVENT_MIN_HEIGHT,
  EVENT_TABLE_DELIMITER_SPACE,
} from '../../common/constants';
import { EVENT_TYPE } from '../../common/enums';
import { EventButtonProps } from './EventButton.props';
import { calculateHeaderAfterDrag, onMoveHeader } from './utils/draggingHeader';
import {
  calculateMonthEventAfterDrag,
  onMoveMonthEvent,
} from './utils/draggingMonth';
import {
  calculateNewTimeWeekDay,
  onMoveNormalEvent,
} from './utils/draggingWeek';
import {
  disableTouchDragging,
  eventButtonInitialState,
  initEventButtonPosition,
} from './EventButton.utils';
import { formatDateTimeToString } from '../../utils/common';
import { onFinishDraggingInternal } from './utils/draggingGeneral';
import { parseEventColor } from '../../utils/calendarDays';
import { useHeight } from '../../utils/layout';
import ButtonBase from '../buttonBase/ButtonBase';
import EventAgenda from './eventAgenda/EventAgenda';
import EventMonth from './eventMonth/EventMonth';
import EventNormal from './eventNormal/EventNormal';
import EventShowMoreMonth from './eventShowMoreMonth/EventShowMoreMonth';
import stateReducer from '../../utils/stateReducer';

// ref to cancel timout
let timeoutRef: any;

const EventButton = (props: EventButtonProps) => {
  const { event, type, day = DateTime.now(), index } = props;
  const { startAt } = event;

  const [state, dispatchState]: any = useReducer(
    stateReducer,
    eventButtonInitialState
  );
  const setState = (stateName: string, data: any): void => {
    const payload: any = { stateName, data };
    dispatchState({ state, payload });
  };

  // store values as refs to access them in event listener
  const offsetTopRef = useRef(state.offsetTop);
  const offsetLeftRef = useRef(state.offsetLeft);
  const xShiftIndexRef = useRef(0);
  const yShiftIndexRef = useRef(0);
  const draggingRef = useRef(false);
  const eventWasChangedRef = useRef(false);

  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };

  const { width, calendarDays, config, daysViewLayout, callbacks } =
    store as Store;

  const heightHook: number = useHeight();

  const { hourHeight, isDark } = config as Config;
  const { onEventClick, onEventDragFinish } = callbacks;

  const columnWidth: number =
    width / (type === EVENT_TYPE.MONTH ? 7 : calendarDays.length);
  const eventColor: string = parseEventColor(event.color as string, isDark);

  const isLoaded: boolean = state.width !== 0 && state.height > 0;
  const style: EventStyle = {
    position:
      type === EVENT_TYPE.AGENDA || type === EVENT_TYPE.SHOW_MORE_MONTH
        ? 'relative'
        : 'absolute',
    height: state.height || EVENT_MIN_HEIGHT,
    width: state.width,
    top: state.offsetTop,
    left: state.offsetLeft,
    zIndex: state.zIndex,
    border: state.zIndex > 2 ? `solid 1px white` : `solid 1px ${eventColor}`,
    backgroundColor: eventColor,
    visibility: isLoaded ? 'visible' : 'hidden',
    // alignItems: meta?.centerText ? 'center' : 'inherit',
  };

  const handleEventClick = (e: any) => {
    e.preventDefault();
    e.stopPropagation();

    // eslint-disable-next-line @typescript-eslint/no-use-before-define
    if (draggingRef.current) {
      // eslint-disable-next-line @typescript-eslint/no-use-before-define
      draggingRef.current = false;
      return;
    }

    if (onEventClick) {
      onEventClick(event);
    }
  };

  const setLayout = (layout: EventLayout) => {
    setState('initialTop', layout.offsetTop);
    setState('initialLeft', layout.offsetLeft);
    setState('offsetTop', layout.offsetTop);
    setState('offsetLeft', layout.offsetLeft);
    setState('drawingY', layout.offsetTop);
    setState('startAt', startAt);
    setState('width', layout.width);
    setState('height', layout.height);
    setState('zIndex', layout.zIndex);
    setState('border', layout.border);
    setState('meta', layout.meta);
  };

  useEffect(() => {
    initEventButtonPosition(type, props.day, event, store, setLayout, index);
  }, []);

  useEffect(() => {
    initEventButtonPosition(type, props.day, event, store, setLayout, index);
  }, [
    // @ts-ignore
    daysViewLayout?.[formatDateTimeToString(props.day || DateTime.now())]?.[
      event.id
    ],
  ]);

  useEffect(() => {
    initEventButtonPosition(type, props.day, event, store, setLayout, index);
  }, [store.layoutUpdateSequence]);

  const initMove = () => {
    if (type === EVENT_TYPE.AGENDA) {
      return;
    }

    if (!draggingRef.current) {
      draggingRef.current = true;
    }

    if (type === EVENT_TYPE.NORMAL) {
      setState('offsetLeft', 0);
      setState('width', columnWidth - EVENT_TABLE_DELIMITER_SPACE);
    }
  };

  const onMove = (e: any) => {
    e.preventDefault();
    e.stopPropagation();

    if (disableTouchDragging(e)) {
      return;
    }
    switch (type) {
      case EVENT_TYPE.NORMAL:
        onMoveNormalEvent(
          e,
          draggingRef,
          day,
          columnWidth,
          width,
          eventWasChangedRef,
          xShiftIndexRef,
          offsetLeftRef,
          offsetTopRef,
          setState
        );
        break;
      case EVENT_TYPE.HEADER:
        onMoveHeader(
          e,
          columnWidth,
          width,
          xShiftIndexRef,
          offsetLeftRef,
          eventWasChangedRef,
          setState
        );
        break;
      case EVENT_TYPE.MONTH:
        onMoveMonthEvent(
          e,
          heightHook,
          draggingRef,
          day,
          columnWidth,
          width,
          xShiftIndexRef,
          yShiftIndexRef,
          eventWasChangedRef,
          offsetLeftRef,
          offsetTopRef,
          setState,
          index || 0
        );
        break;
      default:
        return;
    }
  };

  /**
   * Cancel dragging event
   * remove listeners clean long click timeout and reset state
   * @param e
   */
  const onMouseUp = (e: any) => {
    // clean listeners
    document.removeEventListener('mouseup', onMouseUp, true);
    document.removeEventListener('mousemove', onMove, true);

    // clear timeout
    clearTimeout(timeoutRef);

    if (!eventWasChangedRef.current) {
      setState('offsetLeft', state.offsetLeft);
      setState('width', state.width);
      setState('isDragging', false);
      draggingRef.current = false;

      return;
    }

    eventWasChangedRef.current = false;

    if (!draggingRef.current) {
      return;
    }

    setTimeout(() => {
      draggingRef.current = false;
      setState('isDragging', false);
    }, 100);

    // add data to callback
    if (onEventDragFinish) {
      let newEvent: CalendarEvent | null = null;
      if (type === EVENT_TYPE.NORMAL) {
        newEvent = calculateNewTimeWeekDay(
          offsetTopRef.current,
          offsetLeftRef.current,
          xShiftIndexRef,
          event,
          hourHeight
        );
      } else if (type === EVENT_TYPE.HEADER) {
        newEvent = calculateHeaderAfterDrag(
          calendarDays,
          event,
          xShiftIndexRef
        );
        // eslint-disable-next-line @typescript-eslint/no-use-before-define
      } else if (type === EVENT_TYPE.MONTH) {
        newEvent = calculateMonthEventAfterDrag(
          calendarDays,
          yShiftIndexRef,
          xShiftIndexRef,
          event
        );
      }

      if (newEvent) {
        onFinishDraggingInternal(
          event,
          newEvent,
          store,
          setContext,
          type,
          onEventDragFinish
        );
      }
    }

    e.preventDefault();
    e.stopPropagation();
  };

  /**
   * Start event dragging on long press/touch
   * Set listeners
   * @param e
   */
  const onMouseDownLong = (e: any) => {
    if (disableTouchDragging(e)) {
      return;
    }
    setState('isDragging', true);
    draggingRef.current = true;

    e.preventDefault();
    e.stopPropagation();

    if (e.button !== 0) return;
    document.addEventListener('mousemove', onMove, true);
    document.addEventListener('mouseup', onMouseUp, true);

    // set temp state while dragging
    initMove();
  };

  /**
   * Initial long press click/touch on event
   * @param e
   */
  const onMouseDown = (e: any) => {
    e.preventDefault();
    e.stopPropagation();

    // add timeout to differentiate from normal clicks
    timeoutRef = setTimeout(() => {
      onMouseDownLong(e);
    }, 120);
  };

  return (
    <ButtonBase
      id={event.id}
      isDark={isDark}
      style={style}
      className={`Kalend__Event-${type} ${
        state.isDragging ? 'Kalend__EventButton__elevation' : ''
      } ${store.layoutUpdateSequence === 1 ? 'Kalend__Event__animate' : ''}`}
      onClick={handleEventClick}
      onMouseDown={onMouseDown}
      onMouseUp={onMouseUp}
      onTouchStart={onMouseDown}
      onTouchMove={onMove}
      onTouchEnd={onMouseUp}
    >
      {type === EVENT_TYPE.MONTH || type === EVENT_TYPE.HEADER ? (
        <EventMonth event={event} isDark={isDark} type={type} />
      ) : null}
      {type === EVENT_TYPE.NORMAL ? (
        <EventNormal
          event={event}
          isDark={isDark}
          type={type}
          meta={state.meta}
        />
      ) : null}
      {type === EVENT_TYPE.AGENDA ? (
        <EventAgenda event={event} isDark={isDark} type={type} />
      ) : null}
      {type === EVENT_TYPE.SHOW_MORE_MONTH ? (
        <EventShowMoreMonth event={event} isDark={isDark} type={type} />
      ) : null}
    </ButtonBase>
  );
};

export default EventButton;
import { CALENDAR_OFFSET_LEFT } from '../../../common/constants';
import { CalendarEvent } from '../../../common/interface';
import { DateTime } from 'luxon';

export const calculateHeaderAfterDrag = (
  calendarDays: DateTime[],
  event: CalendarEvent,
  xShiftIndexRef: any
): CalendarEvent => {
  const originalStartAtDateTime = DateTime.fromISO(event.startAt);
  const originalEndAtDateTime = DateTime.fromISO(event.endAt);

  const newDay: DateTime = calendarDays.map(
    (calendarDay: DateTime) => calendarDay
  )[xShiftIndexRef.current];

  const diffInMinutes: number | undefined = originalEndAtDateTime
    .diff(originalStartAtDateTime, 'minutes')
    .toObject().minutes;

  const newStartAt: DateTime = originalStartAtDateTime.set({
    year: newDay.year,
    day: newDay.day,
    month: newDay.month,
    hour: originalStartAtDateTime.hour,
    minute: originalStartAtDateTime.minute,
  });

  // set correct endAt
  const newEndAt: DateTime = newStartAt.plus({ minutes: diffInMinutes });

  return {
    ...event,
    startAt: newStartAt.toUTC().toString(),
    endAt: newEndAt.toUTC().toString(),
  };
};

export const onMoveHeader = (
  e: any,
  columnWidth: number,
  width: number,
  xShiftIndexRef: any,
  offsetLeftRef: any,
  eventWasChangedRef: any,
  setState: any
) => {
  const tableElement: any = document.querySelector('.Kalend__Calendar__table');
  const tableElementRect = tableElement.getBoundingClientRect();

  const touches: any = e.nativeEvent?.touches?.[0];

  // set basic coordinates from movement
  let x: number;

  // handle touch movement
  if (touches) {
    x = touches.clientX - tableElementRect.x;
  } else {
    // calculate x coordinates while following mouse move
    x = e.clientX - tableElementRect.x;
  }

  // prevent free dragging across columns with simple recalculation for
  const columnShift = Math.floor(x / columnWidth);

  const xTable = e.clientX - tableElementRect.x - CALENDAR_OFFSET_LEFT;

  const columnShiftTable = Math.round(xTable / columnWidth);

  if (
    columnShiftTable * columnWidth + CALENDAR_OFFSET_LEFT >= width ||
    xTable < 0
  ) {
    return;
  }

  // prevent event overflowing on last day
  // TODO reset back after moving left again
  // if (columnShift === calendarDays.length) {
  //   setState('width', columnWidth)
  // }

  xShiftIndexRef.current = columnShift;
  setState('offsetLeft', columnShift * columnWidth + CALENDAR_OFFSET_LEFT);
  eventWasChangedRef.current = true;
  offsetLeftRef.current = x;
};
import { CalendarEvent } from '../../../common/interface';
import { DateTime } from 'luxon';

export const calculateMonthEventAfterDrag = (
  calendarDays: DateTime[],
  yShiftIndexRef: any,
  xShiftIndexRef: any,
  event: CalendarEvent
): CalendarEvent => {
  // get new date from x, y coordinates

  // split calendarDays by rows
  const rowCalendarDays: DateTime[][] = [];

  let tempArray: DateTime[] = [];

  calendarDays.forEach((calendarDay: DateTime) => {
    if (
      tempArray.length === 7 ||
      (rowCalendarDays.length === 5 && tempArray.length === 6)
    ) {
      tempArray.push(calendarDay);
      rowCalendarDays.push(tempArray);
      tempArray = [];
    }

    tempArray.push(calendarDay);
  });

  // get correct row by y coordinate
  const matchingRow: DateTime[] = rowCalendarDays[yShiftIndexRef.current];

  // get day by x coordinate
  const matchingDay: DateTime = matchingRow[xShiftIndexRef.current];

  const originalStartAtDateTime = DateTime.fromISO(event.startAt);
  const originalEndAtDateTime = DateTime.fromISO(event.endAt);

  const diffInMinutes: number | undefined = originalEndAtDateTime
    .diff(originalStartAtDateTime, 'minutes')
    .toObject().minutes;

  const newStartAt: DateTime = originalStartAtDateTime.set({
    year: matchingDay.year,
    day: matchingDay.day,
    month: matchingDay.month,
    hour: originalStartAtDateTime.hour,
    minute: originalStartAtDateTime.minute,
  });

  // set correct endAt
  const newEndAt: DateTime = newStartAt.plus({ minutes: diffInMinutes });

  return {
    ...event,
    startAt: newStartAt.toUTC().toString(),
    endAt: newEndAt.toUTC().toString(),
  };
};

export const onMoveMonthEvent = (
  e: any,
  height: number,
  draggingRef: any,
  day: DateTime,
  columnWidth: number,
  width: number,
  xShiftIndexRef: any,
  yShiftIndexRef: any,
  eventWasChangedRef: any,
  offsetLeftRef: any,
  offsetTopRef: any,
  setState: any,
  index: number
) => {
  const columnHeight: number = height / 6;
  if (!draggingRef.current) {
    return;
  }

  if (!day) {
    return;
  }

  const tableElement: any = document.querySelector('.Kalend__Calendar__table');
  const tableElementRect = tableElement.getBoundingClientRect();

  const xTable = e.clientX - tableElementRect.x;
  const yTable = e.clientY - tableElementRect.y;

  // Get column element for day, where event is placed

  const touches: any = e.nativeEvent?.touches?.[0];

  // set basic coordinates from movement
  let x: number;
  let y: number;

  // handle touch movement
  if (touches) {
    x = touches.clientX - tableElementRect.x;
    y = touches.clientY - tableElementRect.y;
  } else {
    // calculate x coordinates while following mouse move
    x = e.clientX - tableElementRect.x;
    y = e.clientY - tableElementRect.y;
  }

  const yReal = yTable - index * columnHeight;

  // prevent free dragging across columns with simple recalculation for
  const columnShiftX = Math.floor(x / columnWidth);
  const columnShiftY = Math.floor(yReal / columnHeight);

  const columnShiftXParsed = parseInt((xTable / columnWidth).toString());
  const columnShiftYParsed = parseInt((yTable / columnHeight).toString());

  const columnShiftTableX = Math.round(xTable / columnWidth);

  if (
    columnShiftTableX * columnWidth >= width ||
    xTable < 0 ||
    columnShiftY * columnHeight >= height ||
    yTable < 0 ||
    y >= height
  ) {
    return;
  }

  xShiftIndexRef.current = columnShiftXParsed;
  yShiftIndexRef.current = columnShiftYParsed;

  setState('offsetLeft', columnShiftX * columnWidth);
  setState('offsetTop', columnShiftY * columnHeight);
  eventWasChangedRef.current = true;
  offsetLeftRef.current = x;
  offsetTopRef.current = y;
};
import {
  CALENDAR_OFFSET_LEFT,
  EVENT_MIN_HEIGHT,
} from '../../../common/constants';
import { CalendarEvent } from '../../../common/interface';
import { DateTime } from 'luxon';

export const calculateNewTimeWeekDay = (
  offsetTopValue: number,
  offsetLeftValue: number,
  xShiftIndexRef: any,
  event: CalendarEvent,
  hourHeight: number
): CalendarEvent => {
  const originalStartAtDateTime = DateTime.fromISO(event.startAt);
  const originalEndAtDateTime = DateTime.fromISO(event.endAt);

  let goingForward = false;
  if (offsetLeftValue >= 0) {
    goingForward = true;
  } else {
    goingForward = false;
  }

  let newDay: DateTime;

  if (goingForward) {
    newDay = originalStartAtDateTime.plus({ days: xShiftIndexRef.current });
  } else {
    newDay = originalStartAtDateTime.minus({
      days: Math.abs(xShiftIndexRef.current),
    });
  }

  const diffInMinutes: number | undefined = originalEndAtDateTime
    .diff(originalStartAtDateTime, 'minutes')
    .toObject().minutes;
  const minutesOffset: number = (offsetTopValue / hourHeight) * 60;

  // add minutes calculated from new offset top
  const newStartAt: DateTime = originalStartAtDateTime
    .set({
      year: newDay.year,
      day: newDay.day,
      month: newDay.month,
      hour: 0,
      minute: 0,
    })
    .plus({ minutes: minutesOffset });

  // set correct endAt
  const newEndAt: DateTime = newStartAt.plus({ minutes: diffInMinutes });

  return {
    ...event,
    startAt: newStartAt.toUTC().toString(),
    endAt: newEndAt.toUTC().toString(),
  };
};

export const onMoveNormalEvent = (
  e: any,
  draggingRef: any,
  day: DateTime,
  columnWidth: number,
  width: number,
  eventWasChangedRef: any,
  xShiftIndexRef: any,
  offsetLeftRef: any,
  offsetTopRef: any,
  setState: any
) => {
  if (!draggingRef.current) {
    return;
  }

  if (!day) {
    return;
  }

  const tableElement: any = document.querySelector('.Kalend__Calendar__table');
  const tableElementRect = tableElement.getBoundingClientRect();

  // Get column element for day, where event is placed
  const dayElement: any = document.getElementById(
    `Kalend__day__${day.toString()}`
  );
  if (!dayElement) {
    return;
  }
  const dayElementRect = dayElement.getBoundingClientRect();

  const touches: any = e.nativeEvent?.touches?.[0];

  // set basic coordinates from movement
  let x: number;
  let y: number;

  // handle touch movement
  if (touches) {
    x = touches.clientX - dayElementRect.x;
    y = touches.clientY - dayElementRect.top;
  } else {
    // handle mouse movement
    // calculate x and y coordinates while following mouse move
    x = e.clientX - dayElementRect.x;
    y = e.clientY - dayElementRect.top;
  }

  // prevent free dragging across columns with simple recalculation for
  const columnShift = Math.floor(x / columnWidth);

  const xTable = e.clientX - tableElementRect.x;

  const columnShiftTable = Math.round(xTable / columnWidth);

  // restrict draggable space for timetable
  if (y < 0) {
    return;
  }

  eventWasChangedRef.current = true;
  setState('offsetTop', y - EVENT_MIN_HEIGHT);
  offsetTopRef.current = y - EVENT_MIN_HEIGHT;

  // prevent overflowing on x-axis
  if (
    columnShiftTable * columnWidth >= width ||
    xTable - CALENDAR_OFFSET_LEFT < 0
  ) {
    return;
  }

  xShiftIndexRef.current = columnShift;
  setState('offsetLeft', columnShift * columnWidth);
  offsetLeftRef.current = x;
};
