import {
  CALENDAR_NAVIGATION_DIRECTION,
  CALENDAR_VIEW,
  WEEKDAY_START,
} from '../common/enums';
import { DateTime } from 'luxon';
import { calculateCalendarDays } from './calendarDays';

/**
 * Calculate new calendar days
 * @param calendarDays
 * @param calendarView
 * @param direction
 * @param weekDayStart
 * @param dispatchContext
 */
export const getNewCalendarDays = async (
  calendarDays: DateTime[],
  calendarView: CALENDAR_VIEW,
  direction: CALENDAR_NAVIGATION_DIRECTION,
  weekDayStart: WEEKDAY_START,
  dispatchContext?: any
): Promise<void> => {
  const setSelectedDate = (date: DateTime) => {
    dispatchContext('selectedDate', date);
  };

  const newCalendarDays: DateTime[] = calculateCalendarDays(
    direction,
    calendarDays,
    calendarView,
    setSelectedDate,
    weekDayStart
  );

  dispatchContext('calendarDays', newCalendarDays);
  // dispatchContext(
  //   'selectedDate',
  //   newCalendarDays[chooseSelectedDateIndex(calendarView)]
  // );
};

export const navigateToToday = async (
  selectedView: CALENDAR_VIEW,
  setContext: any,
  weekDayStart: WEEKDAY_START,
  dateNow: DateTime = DateTime.now()
): Promise<void> => {
  await getNewCalendarDays(
    [dateNow],
    selectedView,
    CALENDAR_NAVIGATION_DIRECTION.TODAY,
    weekDayStart,
    setContext
  );
};
// eslint-disable-next-line @typescript-eslint/no-use-before-define
import React, { useState } from 'react';

import { ButtonIconProps } from './ButtonIcon.props';
import { parseCssDark } from '../../utils/common';

const ButtonIcon = (props: ButtonIconProps) => {
  const [isPressed, setIsPressed] = useState(false);

  const {
    children,
    onClick,
    size,
    disabled,
    isDark,
    iconSize,
    noActive,
    backdropClassName,
    style,
  } = props;

  const handleTouchStart = (): void => setIsPressed(true);

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const handleTouchOff = (e: any): void => setIsPressed(false);

  const containerClassName: string = !size
    ? 'Kalend__ButtonIcon__container'
    : `Kalend__ButtonIcon__container-${size}`;

  const backdropClassNameString: string = backdropClassName
    ? backdropClassName
    : 'Kalend__ButtonIcon__backdrop';

  const buttonClassName: string = noActive
    ? `${
        disabled ? 'Kalend__ButtonIcon__disabled ' : ''
      }Kalend__ButtonIcon-inactive`
    : `${disabled ? 'Kalend__ButtonIcon__disabled ' : ''}Kalend__ButtonIcon`;

  const IconElement: any = React.cloneElement(children, {
    className: `Kalend__ButtonIcon__svg${
      iconSize ? `-${iconSize}` : '-normal'
    }`,
  });

  return (
    <div className={containerClassName} style={style}>
      <button
        className={parseCssDark(buttonClassName, isDark)}
        onClick={onClick}
        disabled={disabled}
        onTouchStart={handleTouchStart}
        onTouchEnd={handleTouchOff}
      >
        {IconElement}
        {isPressed ? <div className={backdropClassNameString} /> : null}
      </button>
    </div>
  );
};

export default ButtonIcon;
import { CalendarViewDropdownProps } from './CalendarViewDropdown.props';
import { Config } from '../../common/interface';
import { Context } from '../../context/store';
import { EvaIcons } from '../eva-icons';
import { parseCalendarViewToText, parseCssDark } from '../../utils/common';
import { useContext, useState } from 'react';
import { useTranslation } from 'react-i18next';
import ButtonBase from '../buttonBase/ButtonBase';
import ButtonIcon from '../buttonIcon/ButtonIcon';
import HeaderCalendarButtons, {
  isSingleView,
} from '../headerCalendarButtons/HeaderCalendarButtons';

const CalendarViewDropdown = (props: CalendarViewDropdownProps) => {
  const { t } = useTranslation();

  const { setViewChanged } = props;

  const [isOpen, setOpen] = useState(false);

  const [store] = useContext(Context);
  const { config, isMobile, selectedView } = store;
  const { isDark, disabledViews, disableMobileDropdown } = config as Config;

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const preventDefault = (e: any) => {
    e.preventDefault();
    e.stopPropagation();
  };

  return (isMobile && (disableMobileDropdown || isSingleView(disabledViews))) ||
    isSingleView(disabledViews) ? null : (
    <>
      <div className={'Kalend__CalendarViewDropdown__wrapper'}>
        {isMobile ? (
          <ButtonIcon isDark={isDark} key={'calendar'} onClick={handleOpen}>
            <EvaIcons.More className={parseCssDark('icon-svg', isDark)} />
          </ButtonIcon>
        ) : (
          <ButtonBase
            isDark={isDark}
            className={'Kalend__ButtonBase-border'}
            onClick={handleOpen}
            text={t(parseCalendarViewToText(selectedView))}
          />
        )}
        {isOpen ? (
          <div
            className={'Kalend__CalendarViewDropdown__backdrop'}
            onClick={handleClose}
          />
        ) : null}
        {isOpen ? (
          <div className={'Kalend__CalendarViewDropdown__container'}>
            <div
              className={'Kalend__CalendarViewDropdown__container-content'}
              onClick={preventDefault}
            >
              <HeaderCalendarButtons
                disabledViews={disabledViews}
                setViewChanged={setViewChanged}
                handleClose={handleClose}
                isForcedMobile={true}
              />
            </div>
          </div>
        ) : null}
      </div>
    </>
  );
};

