import { CalendarEvent } from '../../../common/interface';
import { Context } from '../../../context/store';
import { DateTime } from 'luxon';
import { MONTH_EVENT_HEIGHT } from '../../../common/constants';
import { MonthViewButtonMoreProps } from './MonthViewButtonMore.props';
import { formatDateTimeToString } from '../../../utils/common';
import { useContext } from 'react';
import { useTranslation } from 'react-i18next';
import ButtonBase from '../../buttonBase/ButtonBase';
import ShowMoreModal from '../showMoreModal/ShowMoreModal';

const MonthViewButtonMore = (props: MonthViewButtonMoreProps) => {
  const { t } = useTranslation();

  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };

  const { width, monthOverflowEvents, config, showMoreEvents } = store;

  const { calendarDays } = props;

  const colWidth: number = width / 7;

  const handleClick = (day: DateTime, events: CalendarEvent[]) => {
    setContext('showMoreEvents', { day, events });
  };

  const renderShowMoreButtons = () => {
    if (!monthOverflowEvents || !calendarDays || calendarDays.length === 0) {
      return [];
    }

    return calendarDays.map((calendarDay: DateTime) => {
      const dateKey = formatDateTimeToString(calendarDay);
      const events: CalendarEvent[] | undefined = monthOverflowEvents[dateKey];

      if (events) {
        return (
          <ButtonBase
            key={calendarDay.toString()}
            className={'Kalend__Monthview_Event'}
            style={{
              width: colWidth,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              paddingTop: 3,
              paddingBottom: 3,
              height: MONTH_EVENT_HEIGHT,
            }}
            onClick={() => handleClick(calendarDay, events)}
            isDark={config.isDark}
          >
            <p className={'Kalend__text'} style={{ fontSize: 11 }}>
              {t('buttons:showMore')}
            </p>
          </ButtonBase>
        );
      } else {
        return (
          <div
            key={calendarDay.toString()}
            style={{ width: colWidth, visibility: 'hidden' }}
          />
        );
      }
    });
  };

  const showMoreButtons: any = renderShowMoreButtons();

  return (
    <div
      style={{
        width: '100%',
        display: 'flex',
        flexDirection: 'row',
        position: 'absolute',
        bottom: 5,
        left: 0,
      }}
    >
      {showMoreButtons}
      {showMoreEvents ? <ShowMoreModal /> : null}
    </div>
  );
};

export default MonthViewButtonMore;
import { Context } from '../../../context/store';
import { EVENT_TYPE } from '../../../common/enums';
import { useContext } from 'react';
import EventButton from '../../eventButton/EventButton';

const ShowMoreModal = () => {
  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };

  const { showMoreEvents } = store;

  const renderMoreEvents = () => {
    if (!showMoreEvents) {
      return [];
    }

    return showMoreEvents.events.map((item: any) => {
      return (
        <EventButton
          key={item.id}
          event={item}
          type={EVENT_TYPE.SHOW_MORE_MONTH}
        />
      );
    });
  };

  const events: any = renderMoreEvents();

  const handleClose = () => {
    setContext('showMoreEvents', null);
  };

  const preventDefault = (e: any) => {
    e.preventDefault();
    e.stopPropagation();
  };

  return (
    <div className={'Kalend__ShowMoreModal__wrapper'} onClick={handleClose}>
      <div
        className={'Kalend__ShowMoreModal__container'}
        onClick={preventDefault}
      >
        <h6
          style={{
            fontSize: 16,
            padding: 0,
            margin: 4,
            marginBottom: 8,
            textAlign: 'center',
          }}
        >
          {showMoreEvents.day.toFormat('dd. MMM')}
        </h6>
        {events}
      </div>
    </div>
  );
};

export default ShowMoreModal;
import { Context } from '../context/store';
import { getHeight, getWidth, useHeight, useWidth } from '../utils/layout';
import { getTableOffset } from '../utils/common';
import { useContext, useEffect } from 'react';

const DimensionsLayoutLayer = (props: { children: any }) => {
  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };
  const { selectedView } = store;

  const width: any = useWidth();
  const height: any = useHeight();

  // init context
  useEffect(() => {
    setContext('height', height);
    setContext('width', width - getTableOffset(selectedView));

    if (width < 750) {
      setContext('isMobile', true);
    } else {
      setContext('isMobile', false);
    }
  }, []);

  useEffect(() => {
    setContext('width', getWidth() - getTableOffset(selectedView));

    if (width < 750) {
      setContext('isMobile', true);
    } else {
      setContext('isMobile', false);
    }
  }, [width]);
  useEffect(() => {
    setContext('height', getHeight());
  }, [height]);

  // needs to be separate due to inconsistency for month view
  useEffect(() => {
    setContext('height', getHeight());
  }, [selectedView, selectedView]);

  return props.children;
};

export default DimensionsLayoutLayer;
import { initReactI18next } from 'react-i18next';
import { useEffect } from 'react';
import de from '../locales/de.json';
import en from '../locales/en.json';
import es from '../locales/es.json';
import fr from '../locales/fr.json';
import i18n from 'i18next';
import ru from '../locales/ru.json';
import zh from '../locales/zh.json';

const LanguageLayer = (props: { children: any; customLanguage?: any }) => {
  useEffect(() => {
    const resources = {
      en,
      de,
      fr,
      es,
      zh,
      ru,
      customLanguage: props.customLanguage,
    };

    i18n.use(initReactI18next).init({
      resources,
      lng: 'en',
      fallbackLng: 'en',
      interpolation: {
        escapeValue: false,
      },
    });
  }, []);

  return props.children;
};

export default LanguageLayer;
